mapper.update:
UPDATE ebd_file_cmt
SET content=#{content}
WHERE num=#{num}


UPDATE ebd_file_cmt
SET deleted='yes'
WHERE num=#{num}

mapper.select:
SELECT num, writer, content, ref_group, cmt_group, deleted, regdate
FROM ebd_file_cmt
WHERE num=#{num}

SELECT NVL(MAX(ROWNUM), 0)
FROM ebd_file_cmt
WHERE ref_group=#{ref_group}
